mapper.insert:
insert into employees (emp_no, birth_date, first_name, 
  last_name, gender, hire_date
  )
values (#{emp_no,jdbcType=INTEGER}, #{birth_date,jdbcType=DATE}, #{first_name,jdbcType=VARCHAR}, 
  #{last_name,jdbcType=VARCHAR}, #{gender,jdbcType=CHAR}, #{hire_date,jdbcType=DATE}
  )

mapper.select:
select emp_no, birth_date, first_name, last_name, gender, hire_date
from employees
where emp_no = #{emp_no,jdbcType=INTEGER}

